typeql:
match
$u isa user;
$q isa question, has up_votes $uv, has down_votes $dv;
asked (asker: $u, asked_question: $q);
$uv > 0;
$dv > 0;
fetch {
  "display_name": $u.display_name,
  "user_id": $u.user_id,
  "link": $u.user_link
};

match
$u isa user;
asked (asker: $u, asked_question: $q);
$q isa question;
reduce $count = count($q) groupby $u;
sort $count desc;
limit 5;
fetch {
  "userName": $u.display_name,
  "questionCount": $count
};

match
$q isa question, has created_at $ca;
$ca < 2018-01-01T00:00:00;
tagged (tagged_question: $q, tag: $t);
$t isa tag;
reduce $count = count($q) groupby $t;
sort $count desc;
limit 3;
fetch {
  "tag_name": $t.tag_name,
  "question_count": $count
};

match
$q isa question, has score $s;
$s <= 0;
tagged (tagged_question: $q, tag: $t);
$t isa tag, has tag_name "aws-lambda";
sort $s desc;
limit 3;
fetch {
  "title": $q.title,
  "link": $q.link,
  "score": $s
};

match
$q isa question, has up_votes $uv;
$t isa tag, has tag_name "graphql";
tagged (tagged_question: $q, tag: $t);
sort $uv desc;
limit 5;
fetch {
  "title": $q.title,
  "up_votes": $uv
};

match
$q isa question, has created_at $ca;
sort $ca desc;
limit 3;
fetch {
  "title": $q.title,
  "createdAt": $ca,
  "link": $q.link
};

match
$q isa question, has score $s, has created_at $ca;
$s > 3;
$t isa tag, has tag_name "node.js";
tagged (tagged_question: $q, tag: $t);
sort $ca asc;
limit 3;
fetch {
  "question_id": $q.question_id,
  "title": $q.title,
  "score": $q.score,
  "created_at": $q.created_at
};

match
