reduce $count = count($q) groupby $u;
sort $count desc;
limit 5;
fetch {
  "user": $u.display_name,
  "question_count": $count
};

match
$u isa user;
not { $u has image $img; };
asked (asker: $u, asked_question: $q);
$q isa question, has created_at $ca;
sort $ca;
limit 3;
fetch {
  "title": $q.title,
  "link": $q.link,
  "created_at": $ca
