with fun max_fav($u: user) -> integer:
  match
    asked (asker: $u, asked_question: $q);
    $q has favorites $f;
  return max($f);
with fun min_fav($u: user) -> integer:
  match
    asked (asker: $u, asked_question: $q);
    $q has favorites $f;
  return min($f);
match
  $u isa user;
let $max = max_fav($u);
let $min = min_fav($u);
let $variation = $max - $min;
sort $variation desc;
limit 3;
fetch {
  "user": $u.display_name,
  "favoritesVariation": $variation
};

