with fun answered_count($u: user) -> integer:
  match
  asked (asker: $u, asked_question: $q);
  $q isa question, has answered true;
  return count;
with fun total_count($u: user) -> integer:
  match
  asked (asker: $u, asked_question: $q);
  $q isa question;
  return count;
match
