  "createdAt": $created_at,
  "score": $score
};

match
$u isa user, has user_id $uid, has display_name $dn;
$q isa question, has title $t;
$t like ".*variables.*";
asked (asker: $u, asked_question: $q);
sort $uid;
limit 5;
fetch {
  "display_name": $dn,
  "id": $uid
};

match
$q isa question, has answered true, has created_at $ca;
$t isa tag, has tag_name "graphql";
tagged (tagged_question: $q, tag: $t);
sort $ca desc;
limit 5;
fetch {
  "title": $q.title,
  "link": $q.link,
  "createdAt": $ca
};

match
$u isa user, has display_name $dn;
$dn like "A.*";
$q isa question, has created_at $ca;
asked (asker: $u, asked_question: $q);
sort $ca;
limit 3;
fetch {
  "title": $q.title,
  "createdAt": $ca,
  "display_name": $dn
};
