typeql:
match
$o isa organization, has nbr_employees $n;
$n > 100;
fetch {
  "name": $o.name,
  "nbr_employees": $n
};

match
$c isa city, has city_name "Seattle";
located_in (organization: $org, city: $c);
$org isa organization;
subsidiary_of (parent: $org, subsidiary: $sub);
reduce $numSubsidiaries = count($sub) groupby $org;
sort $numSubsidiaries desc;
limit 3;
fetch { "organizationName": $org.name, "numSubsidiaries": $numSubsidiaries };

match
$co isa country, has country_name "United States of America";
$c isa city;
location-contains (parent: $co, child: $c);
$c has city_name $cn;
limit 3;
fetch {
  "city_name": $cn
};

match
$o isa organization, has organization_id $oid;
$ceo isa person;
ceo_of (organization: $o, ceo: $ceo);
sort $oid;
limit 3;
fetch {
  "organization": $o.name,
  "ceo": $ceo.name
};

match
$o isa organization, has revenue $r;
$r > 100000000;
sort $r desc;
limit 5;
fetch {
  "name": $o.name,
  "revenue": $r
};

match
$o isa organization, has name "Accenture";
mentions (article: $a, mentioned: $o);
$a has date $d;
sort $d asc;
limit 3;
fetch {
  "title": $a.title,
  "date": $d,
  "author": $a.author
};

match
$o isa organization;
$c isa city, has city_name "Seattle";
located_in (organization: $o, city: $c);
fetch {
  "organizationName": $o.name
};

match
$ic isa industry_category;
limit 5;
fetch {
  "name": $ic.industry_category_name
};

match
$ceo isa person, has name "Julie Spellman Sweet";
$org isa organization, has summary $s;
$s like ".*Accenture.*";
ceo_of (ceo: $ceo, organization: $org);
fetch {
  "name": $org.name,
  "summary": $s
};

match
$org isa organization, has name $org_name;
$ceo isa person;
ceo_of (organization: $org, ceo: $ceo);
$a1 isa article, has article_id $aid1;
$a2 isa article, has article_id $aid2;
$org1 isa organization;
$org2 isa organization;
ceo_of (organization: $org1, ceo: $ceo);
ceo_of (organization: $org2, ceo: $ceo);
mentions (article: $a1, mentioned: $org1);
mentions (article: $a2, mentioned: $org2);
$aid1 != $aid2;
select $org_name;
distinct;
limit 5;
fetch { "organization_name": $org_name };

match
$org isa organization;
$city isa city, has city_name "Seattle";
located_in (organization: $org, city: $city);
$article isa article;
mentions (article: $article, mentioned: $org);
$article has sentiment $s;
sort $s desc;
limit 3;
fetch {
  "title": $article.title,
  "date": $article.date,
  "sentiment": $s
};

match
$o isa organization, has revenue $r;
$r > 100000000;
$p isa person, has summary $s;
$s like ".*Accenture.*";
ceo_of (organization: $o, ceo: $p);
fetch {
  "name": $o.name
};

match
$parentOrg isa organization, has name $parentName;
$subOrg isa organization, has nbr_employees $nbrEmployees;
subsidiary_of (parent: $parentOrg, subsidiary: $subOrg);
sort $nbrEmployees desc;
limit 3;
fetch {
  "ParentOrganization": $parentName,
  "Subsidiary": $subOrg.name,
  "NumberOfEmployees": $nbrEmployees
};

match
$org isa organization, has name $org_name;
$ceo isa person, has name $ceo_name;
ceo_of (organization: $org, ceo: $ceo);
$article isa article, has author $author, has title $article_title, has sentiment $s;
$s > 0.5;
$ceo_name == $author;
fetch {
  "OrganizationName": $org_name,
  "CEOName": $ceo_name,
