with fun latest_article_date($o: organization) -> datetime:
  match
    mentions (article: $a, mentioned: $o);
    $a has date $d;
  return max($d);
match
  $o isa organization;
  let $max_date = latest_article_date($o);
  mentions (article: $a, mentioned: $o);
  $a has date $d;
  $d == $max_date;
  $a has sentiment $s;
  $s > 0.8;
fetch {
  "organization_name": $o.name,
  "sentiment_score": $s,
  "article_date": $d
};

