in_category (organization: $o, category: $ic);
invested_in (organization: $o, investor: $inv);
$inv has name $investor_name;
reduce $count = count($o) groupby $inv, $investor_name;
sort $count desc;
limit 3;
fetch { "Investor": $investor_name, "InvestmentCount": $count };

match
$p isa person, has name $n;
board_member_of (member: $p, organization: $o);
reduce $count = count($o) groupby $p, $n;
match $count > 1;
limit 5;
fetch { "name": $n };

match
  $org isa organization;
  $ceo isa person, has name $ceo_name;
  ceo_of (organization: $org, ceo: $ceo);
  $article isa article, has author $author_name;
  $ceo_name == $author_name;
reduce $article_count = count($article) groupby $org, $ceo_name;
match $article_count > 1;
$org has name $org_name;
fetch { "organizationName": $org_name, "ceoName": $ceo_name, "articleCount": $article_count };

match
$o isa organization, has nbr_employees $e;
$e > 1000;
$c isa city, has city_name $name;
located_in (organization: $o, city: $c);
select $name;
distinct;
sort $name;
limit 3;
fetch { "name": $name };

match
$o isa organization, has motto $m;
$m != "";
limit 3;
fetch {
  "name": $o.name
};

match
$o isa organization;
$c isa city, has city_name "Seattle";
located_in (organization: $o, city: $c);
fetch {
  "name": $o.name
};

match
$org isa organization, has name $org_name, has revenue $rev;
$cat isa industry_category, has industry_category_name "Technology";
in_category (organization: $org, category: $cat);
sort $rev desc;
limit 3;
fetch {
  "organization": $org_name,
  "revenue": $rev
};

match
$o isa organization, has revenue $r;
sort $r desc;
limit 5;
fetch {
  "name": $o.name,
  "revenue": $r
};

match
$o isa organization, has is_dissolved true, has name $n;
sort $n;
limit 3;
fetch {
  "name": $n
};

match
$a isa article;
$c isa chunk, has text $t;
$t like ".*corporate social responsibility.*";
has_chunk (article: $a, chunk: $c);
$o isa organization;
mentions (article: $a, mentioned: $o);
select $o;
distinct;
fetch { "name": $o.name };

match
$o isa organization, has is_public true, has nbr_employees $n, has name $org_name;
ceo_of (organization: $o, ceo: $ceo);
$ceo has name $ceo_name;
sort $n desc;
limit 3;
fetch {
  "CEO": $ceo_name,
  "Organization": $org_name,
  "NumberOfEmployees": $n
};

match
$o isa organization;
$a isa article;
mentions (article: $a, mentioned: $o);
reduce $articleCount = count($a) groupby $o;
sort $articleCount desc;
limit 5;
fetch {
  "organizationName": $o.name,
  "articleCount": $articleCount
};
