cypher:
// Define the current timestamp and the timestamp for one year ago
WITH timestamp() AS current_time, timestamp() - 31536000 AS one_year_ago

// Match users who have asked questions in the last year
MATCH (u:User)-[:ASKED]->(q:Question)
WHERE q.creation_date >= one_year_ago

// Count the number of questions each user has asked
WITH u, COUNT(q) AS question_count

// Order by the number of questions in descending order and limit to the top users
ORDER BY question_count DESC
LIMIT 10

// Return the users and their question counts
RETURN u.display_name AS user, question_count
