MATCH (o:Organization)-[:HAS_SUPPLIER]->(s:Organization) 
WHERE EXISTS {(s)-[:HAS_INVESTOR]->(o)}
RETURN o.name AS organization, s.name AS supplier_investor

MATCH (investor:Organization)-[:HAS_INVESTOR]->(startup:Organization)
WHERE startup.nbrEmployees < 100 AND EXISTS {
  (startup)<-[:MENTIONS]-(:Article {date: date('2022-01-01') < date() < date('2023-01-01')})
}
RETURN investor.name
LIMIT 3
